/** ucontext.h stubs */

#ifndef UCONTEXT_H
#define UCONTEXT_H

union sigval {
  int    sival_int;    /* Integer signal value */
  void  *sival_ptr;    /* Pointer signal value */
};

typedef struct {
  int          si_signo;    /* Signal number */
  int          si_code;     /* Cause of the signal */
  union sigval si_value;    /* Signal value */
} siginfo_t;

typedef struct __ucontext ucontext_t;

#endif

